import { useState } from "react";
import "../css/country.css";
export function CountryDetails() {
  const [country, setCountry] = useState(["India"]);
  let countries = [
      { country: "india", cities: ["Indore", "ujjain", "Dewas", "mumbai"] },
      { country: "australia", cities: ["Indore", "ujjain", "Dewas", "mumbai"] },
      { country: "pakistan", cities: ["Islamabad", "lahore", "karachi"] },
    ];
  let getCt = (e) => {
    //const id = document.getElementById("select");
    //console.log(countries[0].country)

    // setCountry("Aus");  
    countries.map((l)=>{
      console.log(l.country)  
      setCountry([...country,l.country])
    })
  };

  return (
    <div className="country">
      <label style={{ padding: "10px" }}>Choose a Country:</label>
      <div>
        <select id="select" onChange={getCt} name="Country">
          {country.map((l)=>{
          <option value="i" >
            {l}
          </option>
          })}
          <option value="i" >
            Aus
          </option>
        </select>
      </div>
    </div>
  );
}
