function Display({b, ...props}) {
  return (
    <div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {props.list.map((item, i) => (
            <tr key={i}>
              <th scope="row">{i + 1}</th>
              <td
                style={{
                  fontWeight: `${b && 800}`,
                  fontStyle: props.i ? "italic" : " ",
                  textDecoration: props.ul ? "underline" : " ",
                  color: props.c ? "blue" : "red",
                }}
              >
                {item}
              </td>
              <td>
                <input
                  type="button"
                  onClick={() => props.delet(i)}
                  value="Delete"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      .
    </div>
  );
}
export default Display;
