        updatelist(arr => [...arr,name])
        document.getElementById("name").value="";
    }

    return (
        <div>       
                <div class="input-group mb-3">

  <input type="text" class="form-control" id="name"/>
</div>
                <button onClick={add}>Add</button>
                <Display list={list}/>      
        </div>
    )
}
export default Form
