import "../css/convert.css"
import { useEffect } from "react";
import { useState } from "react";
function Convert() {

    let [money,setMoney]=useState(0); 
    function convertMoney()
    {
        let dollar=document.getElementById("dollar").value;
        setMoney(82.44*dollar)
    }
    return (
    <div>
      <div>
        <div class="input-group">
            <label className="lb">Enter Money In Dollar To conver</label>
          <span class="input-group-text sm">$</span>
          <input id="dollar"
            type="text"

            class="form-control"
            aria-label="Amount (to the nearest dollar)"
          />
          <button type="button" class="btn btn-primary" onClick={convertMoney}>Convert</button>
          <span className="money_container">{money}</span>
        </div>

      </div>
    </div>
  );
}
export default Convert;
