import "../css/Edit.css"
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import bold from '../Images/bold.png'
import underline from "../Images/underline.png"
import italic from "../Images/italic.png"
// import { Switch } from "@mui/material";
import ReactSwitch from 'react-switch';
import { useState } from "react";
export function Edit()
{
    const [toggle,setToggle]=useState(true);
    const handleChange=(e)=>{
        console.log(toggle);
        setToggle(e);
    }
    return(
        <div className="edit_container">
            <Stack spacing={2} direction="row">
            <Button variant="text" ><img src={underline} height={20} width={20} onClick={underline}/></Button>
            <Button variant="contained"><img src={bold} height={20} width={20}/></Button>
            <Button variant="outlined">I<img src={italic} height={20} width={20}/></Button>
            <div>
                <ReactSwitch className="react-switch" checked={toggle} onChange={handleChange} color="primary" id="rs"/>
            </div>
        </Stack>
        </div>
    );
}
