function Display(props) {
  let delet=(i)=>
  {

      props.list.splice(i)
  }
  console.log("Hii", props.list);
  return (
    <div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {props.list.map((item, i) => (
            <tr key={i}>
              <th scope="row">{i + 1}</th>
              <td>{item}</td>
              <td>
                <input type="button" onClick={delet(i)}  value="Delete"/>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
export default Display;
