import Display from "./Display";

function Form()
{
    function add()
    {
        let name=document.getElementById("name").value;
        console.log(name);
        <Display name={name}/>
    }

    return (
        <div>
            <form>
                <input type="text" placeholder="Your Name" id="name"/>
                <button onClick={add}>Add</button>
            </form>                
        </div>
    )
}
export default Form
